/* Generated by Freestyle Learning Group */

package freestyleLearningGroup.independent.gui;

import java.awt.Image;
import java.awt.event.ActionListener;

public class FLGEditToolBarButton extends FLGAbstractImageButton {
    protected static Image BUTTON_NORMAL;
    protected static Image BUTTON_PRESSED;
    protected static Image BUTTON_ROLLOVER;
    protected static Image BUTTON_DISABLED;

    public FLGEditToolBarButton(Image image) {
        this(image, null, null);
    }

    public FLGEditToolBarButton(Image image, String toolTipText, ActionListener actionListener) {
        buttonWidth = 30;
        buttonHeight = 30;
        imageWidth = 20;
        imageHeight = 20;
        if (BUTTON_NORMAL == null) {
            BUTTON_NORMAL = createButtonImage("images/editToolBarButtonNormal.gif");
            BUTTON_DISABLED = BUTTON_NORMAL;
            BUTTON_PRESSED = createButtonImage("images/editToolBarButtonPressed.gif");
            BUTTON_ROLLOVER = createButtonImage("images/editToolBarButtonRollover.gif");
        }
        button_normal = BUTTON_NORMAL;
        button_pressed = BUTTON_PRESSED;
        button_rollover = BUTTON_ROLLOVER;
        button_disabled = BUTTON_DISABLED;
        init(image);
        if (toolTipText != null) setToolTipText(toolTipText);
        if (actionListener != null) addActionListener(actionListener);
    }

    // creates a copy of the toolBarButton so that the same button for another learningUnitView
    // need not to be created twice. This can result in a dramatic decrease of the time needed to load
    // the learning unit view managers of a learning unit when several view managers
    // share some toolbar buttons.
    public FLGEditToolBarButton copy(String toolTipText, ActionListener actionListener) {
        FLGEditToolBarButton button = new FLGEditToolBarButton();
        button.image_normal = image_normal;
        button.image_disabled = image_disabled;
        button.image_pressed = image_pressed;
        button.image_rollover = image_rollover;
        button.button_normal = button_normal;
        button.button_pressed = button_pressed;
        button.button_disabled = button_disabled;
        button.button_rollover = button_rollover;
        button.buttonWidth = buttonWidth;
        button.buttonHeight = buttonHeight;
        button.imageWidth = imageWidth;
        button.imageHeight = imageHeight;
        button.initAllButImages();
        if (toolTipText != null) button.setToolTipText(toolTipText);
        if (actionListener != null) button.addActionListener(actionListener);
        return button;
    }

    protected FLGEditToolBarButton() {
    }
}
