package freestyleLearningGroup.independent.gui;

import java.awt.Dimension;
import java.awt.Frame;
import java.awt.Graphics;
import java.awt.Image;
import java.awt.Rectangle;
import java.awt.Toolkit;
import java.awt.Window;
import java.net.URL;

public class FLGSplashWindow extends Window {
    Image splashImage;

    public FLGSplashWindow(Frame frame, URL imageURL) {
        super(frame);
        splashImage = FLGImageUtility.loadImageAndWait(imageURL);
        setSize(splashImage.getWidth(frame), splashImage.getHeight(frame));
        Dimension screenDim = Toolkit.getDefaultToolkit().getScreenSize();
        Rectangle winDim = getBounds();
        setLocation((screenDim.width - winDim.width) / 2, (screenDim.height - winDim.height) / 2);
    }

    public void paint(Graphics g) {
        if (splashImage != null) {
            g.drawImage(splashImage, 0, 0, this);
        }
    }
}
