package freestyleLearningGroup.independent.util;

import java.util.Locale;
import java.util.MissingResourceException;
import java.util.ResourceBundle;

public class FLGInternationalization {
    /**
     * An object of this class provides access to language dependent ressources. The default-resources are stored in a file
     * <i>filename</i> with the suffix <code>.properties</code>. Resources for a specific language
     * are defined in file <code><i>file</i>_<i>countrycode</i>.properties</code>, e.g.
     * <code>FLGMainFrame_de.properties</code>.
     */
    private ResourceBundle resources;

    public FLGInternationalization(String name, ClassLoader classLoader) {
        try {
            if (classLoader != null)
                resources = ResourceBundle.getBundle(name, Locale.getDefault(), classLoader);
            else
                resources = ResourceBundle.getBundle(name, Locale.getDefault(), getClass().getClassLoader());
        }
        catch (MissingResourceException mre) {
            System.out.println("Properties " + name + " - not found.");
            System.exit(1);
        }
    }

    public String getString(String key) {
        String str;
        try {
            str = resources.getString(key);
        }
        catch (MissingResourceException mre) {
            str = null;
        }
        return str;
    }

    /** Returns a character to the key or ' ' if the key is unknown or the entry is longer than one character */
    public char getChar(String key) {
        String str;
        try {
            str = resources.getString(key);
        }
        catch (MissingResourceException mre) {
            str = null;
        }
        char ch = ' ';
        if (str != null && str.length() == 1) ch = str.toCharArray() [0];
        return ch;
    }
}
